SELECT COUNT(LG.LOG_SEQ) AS CNT FROM LOG_GENERAL LG WHERE LG.REG_DT BETWEEN CONCAT(#{fromDate}, ' 00:00:00') AND CONCAT(#{toDate}, ' 23:59:59') AND LG.LOG_ITEM IN
#{item}
AND (LG.CLIENT_ID LIKE CONCAT('%', #{searchKey}, '%') OR LG.USER_ID LIKE CONCAT('%', #{searchKey}, '%') OR LG.LOG_VALUE LIKE CONCAT('%', #{searchKey}, '%'))

SELECT LOG_SEQ, CLIENT_ID, CASE WHEN SUBSTR(USER_ID, 1, 1) = '+' THEN CONCAT('[LU] ', SUBSTRING(USER_ID, 2)) WHEN SUBSTR(USER_ID, 1, 1) = '-' THEN '' ELSE USER_ID END USER_ID, LOG_TP, LOG_ITEM, LOG_VALUE, REG_DT FROM LOG_SECURITY WHERE REG_DT BETWEEN CONCAT(#{fromDate}, ' 00:00:00') AND CONCAT(#{toDate}, ' 23:59:59') AND LOG_ITEM IN
#{item}
AND (CLIENT_ID LIKE CONCAT('%', #{searchKey}, '%') OR USER_ID LIKE CONCAT('%', #{searchKey}, '%') OR LOG_VALUE LIKE CONCAT('%', #{searchKey}, '%'))
AND (CLIENT_ID IN ( SELECT CLIENT_ID FROM CLIENT_MSTR WHERE GRP_ID IN ( SELECT 'CGRPDEFAULT' UNION SELECT GRP_ID FROM CLIENT_GRP_MSTR GM WHERE GM.WHLE_GRP_ID REGEXP (SELECT REPLACE(GROUP_CONCAT(GPATH), ',', '|') AS GPATH FROM (SELECT CONCAT('^', W.WHLE_GRP_ID) GPATH FROM CLIENT_GRP_MSTR W WHERE W.GRP_ID IN (SELECT GRP_ID FROM ADMIN_CLIENTGRP WHERE ADMIN_ID = #{adminId} AND GRP_ID != 'CGRPDEFAULT') ) TT1 ) ) ))
ORDER BY ${paramOrderColumn} ${paramOrderDir} LIMIT #{paramStart}, #{paramLength}
